    if (e.keyCode == 13) {
        if (window.event)
            e.returnValue = false;
        else
            e.preventDefault();
    }
    else {
        if (window.event) {
            if (e.keyCode < 48 || e.keyCode > 57)
                e.returnValue = false;
        }
        else {
            if (e.keyCode == 0 && (e.charCode < 48 || e.charCode > 57))
                e.preventDefault();
        }
    }
}

