function changeFontSize(rootElement, fontSizeAmount) {
    var elmentsToCahnge;

    try {
        for (var i = 0; i < tagsToChange.length; i++) {
            if (rootElement == "")
                elmentsToCahnge = document.getElementsByTagName(tagsToChange[i]);
