function getFonsizeFromSizeElement(size) {
    size = parseInt(size);
    if (size > 7) size = 7;

    switch (size) {
        case 7: return 48;
        case 6: return 32;
        case 5: return 24;
        case 4: return 19;
        case 3: return 16;
        case 2: return 13;
        case 1: return 10;
        default: return 16;
    }
